def solve():
    h, w = map(int, input().split())
    grid = [input() for _ in range(h)]

    goats = []
    for r in range(h):
        for c in range(w):
            if grid[r][c] == 'X':
                goats.append((r, c))

    if not goats:
        print(0)
        return

    min_fences = float('inf')
    for i in range(1 << (h * w)):
        fences = []
        fence_count = 0
        temp_grid = [list(row) for row in grid]
        for r in range(h):
            for c in range(w):
                if (i >> (r * w + c)) & 1 and temp_grid[r][c] == '.':
                    fences.append((r,c))
                    temp_grid[r][c] = '#'
                    fence_count +=1

        escaped = False
        for r, c in goats:
            q = [(r, c)]
            visited = set()
            while q:
                curr_r, curr_c = q.pop(0)
                if (curr_r, curr_c) in visited:
                    continue
                visited.add((curr_r, curr_c))

                if curr_r < 0 or curr_r >= h or curr_c < 0 or curr_c >= w:
                    escaped = True
                    break

                for dr, dc in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                    nr, nc = curr_r + dr, curr_c + dc
                    if 0 <= nr < h and 0 <= nc < w and temp_grid[nr][nc] != '#' and (nr, nc) not in visited:
                        q.append((nr, nc))
            if escaped:
                break
        if not escaped:
            min_fences = min(min_fences, fence_count)

    print(min_fences)

solve()
