#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int calculate(char *expression, int *len) {
    if (isdigit(expression[0])) {
        *len = 1;
        return atoi(expression);
    }
    int len1 = 0, len2 = 0;
    int val1 = 0, val2 = 0;
    int i = 0;
    char op = expression[i++];
    while (expression[i] != ',') i++;
    i++;
    char exp1[1001], exp2[1001];
    int j = 0;
    while (expression[i] != ')') exp1[j++] = expression[i++];
    exp1[j] = '\0';
    val1 = calculate(exp1, &len1);
    i++;
    j = 0;
    while (expression[i] != ')' && expression[i] != '?') exp2[j++] = expression[i++];
    exp2[j] = '\0';
    val2 = calculate(exp2, &len2);

    *len = 2 + len1 + len2 + (strlen(exp1) + strlen(exp2) + 2);

    if (op == '^') return val1 > val2 ? val1 : val2;
    return val1 < val2 ? val1 : val2;
}

int main() {
    int Q;
    scanf("%d", &Q);
    while (Q--) {
        char expression[1001];
        scanf("%s", expression);
        int len = 0;
        int value = calculate(expression, &len);
        printf("%d %d\n", value, len);
    }
    return 0;
}
