#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

int eval(const string& s, int& pos) {
    if (isdigit(s[pos])) {
        int num = 0;
        while (pos < s.length() && isdigit(s[pos])) {
            num = num * 10 + (s[pos] - '0');
            pos++;
        }
        return num;
    } else {
        char op = s[pos++];
        int left = eval(s, pos);
        pos++; 
        int right = eval(s, pos);
        pos++; 
        if (op == '^') return max(left, right);
        else return min(left, right);
    }
}

int main() {
    int q;
    cin >> q;
    cin.ignore(); 

    while (q--) {
        string s;
        getline(cin, s);
        int pos = 0;
        int val = eval(s, pos);
        int min_chars = 0;
        pos = 0;
        int cur_val = -1;
        int chars_read = 0;

        while(cur_val != val){
            cur_val = eval(s, pos);
            chars_read = pos;


            if(s[pos] == '?') break;

            pos = 0;
        }


        cout << val << " " << chars_read +1 << endl;
    }
    return 0;
}
