#include <stdio.h>

int main() {
    int H, W, d;
    scanf("%d %d %d", &H, &W, &d);
    char colors[H][W];
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int sum = i + j;
            colors[i][j] = "RYGB"[(sum % 4)];
        }
    }
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            printf("%c", colors[i][j]);
        }
        printf("\n");
    }
    return 0;
}
