import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int Q = sc.nextInt();
        sc.nextLine(); 

        for (int i = 0; i < Q; i++) {
            String statement = sc.nextLine();
            int value = evaluate(statement);
            int charsRead = minCharsToRead(statement, value);
            System.out.println(value + " " + charsRead);
        }
        sc.close();
    }

    static int evaluate(String statement) {
        String expression = statement.substring(0, statement.length() - 1);
        return eval(expression);
    }

    static int eval(String expression) {
        if (Character.isDigit(expression.charAt(0))) {
            return Integer.parseInt(expression);
        } else {
            int opIndex = expression.indexOf('(');
            char op = expression.charAt(0);
            int commaIndex = expression.indexOf(',');
            int endIndex = expression.indexOf(')');
            int left = eval(expression.substring(opIndex + 1, commaIndex).trim());
            int right = eval(expression.substring(commaIndex + 1, endIndex).trim());

            if (op == '^') {
                return Math.max(left, right);
            } else {
                return Math.min(left, right);
            }
        }
    }

    static int minCharsToRead(String statement, int value) {
        for (int i = 1; i <= statement.length(); i++) {
            String sub = statement.substring(0, i);
            try {
                if (evaluate(sub + "?") == value) {
                    return i;
                }
            } catch (Exception e) {

            }
        }
        return statement.length();
    }
}
