def solve():
    q = int(input())
    for _ in range(q):
        s = input()
        n = len(s)

        def eval_expr(expr):
            if expr.isdigit():
                return int(expr)

            op = expr[0]

            paren_count = 0
            comma_pos = -1
            for i in range(1, len(expr)):
                if expr[i] == '(':
                    paren_count += 1
                elif expr[i] == ')':
                    paren_count -= 1
                elif expr[i] == ',' and paren_count == 0:
                    comma_pos = i
                    break

            left_expr = expr[2:comma_pos]
            right_expr = expr[comma_pos + 1:-1]

            left_val = eval_expr(left_expr)
            right_val = eval_expr(right_expr)

            if op == '^':
                return max(left_val, right_val)
            else:
                return min(left_val, right_val)


        ans = -1
        min_chars = -1

        for i in range(1,n):


            sub_s = s[:i]


            try:

                cur = eval_expr(sub_s[:-1])


                ans = cur
                min_chars = i

                break
            except:
                pass

        if min_chars == -1:
            min_chars = n -1
            ans = eval_expr(s[:-1])

        print(ans, min_chars)

solve()
