#include <stdio.h>
#include <string.h>

int main() {
    char T[100001];
    scanf("%s", T);
    int n = strlen(T);
    int count = 0;
    for (int len = 1; len <= n; len++) {
        for (int i = 0; i <= n - len; i++) {
            int flag = 1;
            for (int j = 0; j < i; j++) {
                int k = 0;
                for (int l = j; l < n; l++) {
                    if (k < len && T[l] == T[i + k]) {
                        k++;
                    }
                    if (k == len) {
                        flag = 0;
                        break;
                    }
                }
                if (!flag) break;
            }
            if (flag) count++;
        }
    }
    printf("%d\n", count);
    return 0;
}
