#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
  string t;
  cin >> t;

  int n = t.length();
  int max_s = 0;

  for (int i = 0; i < (1 << n); ++i) {
    vector<string> s;
    vector<bool> used(n, false);
    int count = 0;

    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        for (int k = j + 1; k <= n; ++k) {
          string sub = t.substr(j, k - j);
          bool ok = true;
          for (int l = 0; l < s.size(); ++l) {
            if (s[l].length() <= sub.length() ) {
              int cur = 0;
              for (int m = 0; m < s[l].length(); ++m) {
                if (cur < sub.length() && s[l][m] == sub[cur]) cur++;
              }
              if (cur == s[l].length()) ok = false;
            }

            if (sub.length() <= s[l].length()) {
              int cur = 0;
              for (int m = 0; m < sub.length(); ++m) {
                if (cur < s[l].length() && sub[m] == s[l][cur]) cur++;
              }
              if (cur == sub.length()) ok = false;
            }
          }
          if (ok) {
              bool used_check = false;
              for(int p=j; p<k; ++p) if(used[p]) used_check = true;
              if(!used_check){
                s.push_back(sub);
                for(int p=j; p<k; ++p) used[p] = true;
                count++;
                break;
              }
          }
        }
      }
    }
    max_s = max(max_s, count);
  }
  cout << max_s << endl;
  return 0;
}
