def solve():
    t = input()
    n = len(t)
    ans = 0
    for i in range(1 << n):
        s = []
        cur = ""
        for j in range(n):
            if (i >> j) & 1:
                cur += t[j]
            else:
                if cur:
                    s.append(cur)
                    cur = ""
        if cur:
            s.append(cur)

        ok = True
        for k in range(len(s)):
            for l in range(len(s)):
                if k != l:
                    if is_subsequence(s[k], s[l]):
                        ok = False
                        break
            if not ok:
                break
        if ok:
            ans = max(ans, len(s))
    print(ans)

def is_subsequence(s1, s2):
    i = 0
    j = 0
    while i < len(s1) and j < len(s2):
        if s1[i] == s2[j]:
            i += 1
        j += 1
    return i == len(s1)

