#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

int main() {
  long long n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  vector<long long> b(n);
  for (int i = 0; i < n; ++i) {
    cin >> b[i];
  }

  long long total_cost = 0;
  vector<long long> diff(n);
  for (int i = 0; i < n; ++i) {
    diff[i] = a[i] - b[i];
  }

  vector<long long> pref(n + 1, 0);
  for (int i = 0; i < n; ++i) {
    pref[i + 1] = pref[i] + diff[i];
  }

  vector<long long> pref_abs(n+1,0);
  for(int i=0; i<n; ++i){
    pref_abs[i+1] = pref_abs[i] + abs(diff[i]);
  }

  long long cur_pref = 0;
  for(int i=0; i<n; ++i){
    total_cost += abs(pref[i+1]-cur_pref)*i;
    cur_pref = pref[i+1];
  }


  long long ans = 0;
  vector<long long> bal(n,0);
  for(int i=0; i<n; ++i){
    if(diff[i]>0){
        bal[i] = diff[i];
    } else {
        bal[i] = -diff[i];
    }
  }

  vector<long long> pref_bal(n+1, 0);
  for(int i=0; i<n; ++i){
    pref_bal[i+1] = pref_bal[i] + bal[i];
  }


  long long cur = 0;
  for(int i=0; i<n; ++i){
    if(diff[i]>0){
        ans += diff[i]*i;
    } else {
        ans += abs(diff[i]) * i;
    }
  }

  long long cur_sum = 0;
  for (int i = 0; i < n; i++) {
    cur_sum += diff[i];
    if (cur_sum > 0) {
        ans -= cur_sum;
    } else {
        ans += cur_sum;
    }
  }


  cout << ans << endl;
  return 0;
}
