import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        long[] b = new long[n];
        long sumA = 0;
        long sumB = 0;
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
            sumA += a[i];
        }
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextLong();
            sumB += b[i];
        }
        long[] diff = new long[n];
        for (int i = 0; i < n; i++) {
            diff[i] = b[i] - a[i];
        }
        long cost = 0;
        long[] pref = new long[n + 1];
        for (int i = 0; i < n; i++) {
            pref[i + 1] = pref[i] + diff[i];
        }
        Arrays.sort(pref);
        long median = pref[n / 2];
        for (int i = 0; i < n; i++) {
            cost += Math.abs(pref[i + 1] - median) * (i + 1);
        }

        System.out.println(cost);
    }
}
