#include <iostream>
#include <vector>

using namespace std;

int main() {
    int h, w, d;
    cin >> h >> w >> d;

    vector<string> grid(h, string(w, ' '));
    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            int color_index = (i + j) % 4;
            if (color_index == 0) grid[i][j] = 'R';
            else if (color_index == 1) grid[i][j] = 'Y';
            else if (color_index == 2) grid[i][j] = 'G';
            else grid[i][j] = 'B';
        }
    }

    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            cout << grid[i][j];
        }
        cout << endl;
    }

    return 0;
}
