#include <stdio.h>

#define MOD 1000000007

long long power(long long base, long long exp) {
    long long res = 1;
    base %= MOD;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % MOD;
        base = (base * base) % MOD;
        exp /= 2;
    }
    return res;
}

int main() {
    int Q;
    scanf("%d", &Q);
    while (Q--) {
        long long N, C;
        scanf("%lld %lld", &N, &C);
        long long ans = N;
        for (long long i = 1; i <= 60; ++i) {
            long long time_spent = (power(2, i) - 1) * C;
            if (time_spent > N) break;
            ans = (ans > (N - time_spent) * power(2, i)) % MOD ? ans : (N - time_spent) * power(2, i);

        }
        printf("%lld\n", ans % MOD);
    }
    return 0;
}
