#include <iostream>

using namespace std;

int main() {
  int q;
  cin >> q;

  while (q--) {
    long long n, c;
    cin >> n >> c;

    long long max_leaflets = 0;
    for (long long i = 0; i <= n; ++i) {
      long long current_leaflets = 0;
      long long time_spent = 0;
      long long num_clones = 1;

      if (i > 0) {
        time_spent += i * c;
      }

      if (time_spent >= n) {
          continue;
      }

      long long remaining_time = n - time_spent;

      long long clones = 1LL << i;
      current_leaflets = remaining_time * clones;

      max_leaflets = max(max_leaflets, current_leaflets);
    }
    cout << max_leaflets % 1000000007 << endl;
  }
  return 0;
}
