#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

int main() {
    int n, k;
    scanf("%d %d", &n, &k);

    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
        a[i]--; 
    }

    long long count = 0;
    int colors[n];

    for (int i = 0; i < pow(k,n); ++i) {
        int temp = i;
        for (int j = 0; j < n; ++j) {
            colors[j] = temp % k;
            temp /= k;
        }
        count++;
    }

    printf("%lld\n", count % MOD);
    return 0;
}
