#include <iostream>
#include <vector>

using namespace std;

int main() {
    long long n, k;
    cin >> n >> k;

    vector<long long> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
        a[i]--; 
    }

    long long mod = 1e9 + 7;

    if (n == 1) {
        cout << k << endl;
        return 0;
    }

    long long count = 0;
    vector<long long> colors(n);
    function<void(long long)> solve = [&](long long index) {
        if (index == n) {
            count = (count + 1) % mod;
            return;
        }

        for (long long i = 0; i < k; ++i) {
            colors[index] = i;
            bool possible = true;
            solve(index + 1);
        }
    };

    solve(0);
    cout << count << endl;

    return 0;
}
