import java.util.*;
import java.math.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt() - 1;
        }

        long ans = 0;
        int[] colors = new int[n];
        boolean[] used = new boolean[k];


        function(0, a, colors, k, used, n, ans);
        System.out.println(ans);


    }

    static void function(int v, int[] a, int[] colors, int k, boolean[] used, int n, long ans){
        if(v == n){
            ans++;
            return;
        }

        for(int i = 0; i < k; i++){
            boolean possible = true;
            colors[v] = i;
            if(colors[a[v]] == colors[v]){
                possible = false;
            }
            if(possible){
                function(v + 1, a, colors, k, used, n, ans);
            }
        }
    }
}
