#include <iostream>
#include <cmath>

using namespace std;

int main() {
  int t;
  cin >> t;

  while (t--) {
    long double xa, ya, ra, xb, yb, rb;
    cin >> xa >> ya >> ra >> xb >> yb >> rb;

    long double dist = sqrt(pow(xa - xb, 2) + pow(ya - yb, 2));
    long double ans = floor(2 * acos((pow(ra,2) + pow(dist,2) - pow(rb,2))/(2*ra*dist))/acos(-1.0));
    cout << (long long)ans << endl;
  }
  return 0;
}
