#include <stdio.h>

int main() {
    long long n;
    scanf("%lld", &n);
    long long a[n];
    long long inv = 0;
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    for (long long i = 0; i < n; i++) {
        for (long long j = i + 1; j < n; j++) {
            if (a[i] > a[j]) {
                inv++;
            }
        }
    }
    printf("%lld\n", inv);
    return 0;
}
