#include <iostream>
#include <vector>

int main() {
    long long n;
    std::cin >> n;
    std::vector<long long> a(n);
    for (long long i = 0; i < n; ++i) {
        std::cin >> a[i];
    }
    long long ans = 0;
    std::vector<long long> b(n + 1);
    for (long long i = 0; i < n; ++i) {
        b[a[i]] = i;
    }
    std::vector<bool> vis(n + 1, false);
    for (long long i = 1; i <= n; ++i) {
        if (!vis[i]) {
            long long len = 0;
            long long cur = i;
            while (!vis[cur]) {
                vis[cur] = true;
                cur = a[b[cur]];
                len++;
            }
            ans += len * (len - 1) / 2;
        }
    }
    std::cout << ans << std::endl;
    return 0;
}

