import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        long ans = 0;
        long[] b = new long[n];
        for (int i = 0; i < n; i++) {
            b[i] = i + 1;
        }
        for (int i = 0; i < n; i++) {
            long diff = Math.abs(a[i] - b[i]);
            ans += diff;
        }
        System.out.println(ans);
    }
}
