#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<int> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  if (n == 2) {
    if (a[0] == 1 && a[1] == 1) {
      cout << "Possible" << endl;
    } else {
      cout << "Impossible" << endl;
    }
    return 0;
  }

  sort(a.begin(), a.end());

  if (a[n - 1] > n -1 || a[0] == 0) {
    cout << "Impossible" << endl;
    return 0;
  }

  if (a[n - 1] != a[n - 2] + 1 && a[n-1] != a[n-2]) {
      cout << "Impossible" << endl;
      return 0;
  }

  for (int i = 0; i < n - 1; ++i) {
      if (a[i] > a[i+1] && a[i] != a[i+1] +1 ) {
          cout << "Impossible" << endl;
          return 0;
      }
  }


  cout << "Possible" << endl;
  return 0;
}
