#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  long long mod = 924844033;

  vector<long long> p(n);
  for (long long i = 0; i < n; ++i) {
    p[i] = i + 1;
  }

  long long count = 0;
  do {
    bool ok = true;
    for (long long i = 0; i < n; ++i) {
      if (abs(p[i] - (i + 1)) == k) {
        ok = false;
        break;
      }
    }
    if (ok) {
      count++;
    }
  } while (next_permutation(p.begin(), p.end()));

  cout << count % mod << endl;
  return 0;
}
