MOD = 924844033

def solve():
    n, k = map(int, input().split())

    fact = [1] * (n + 1)
    for i in range(2, n + 1):
        fact[i] = (fact[i-1] * i) % MOD

    dp = {}
    def count_permutations(idx, used):
        if idx == n + 1:
            return 1

        state = (idx, tuple(sorted(used)))
        if state in dp:
            return dp[state]

        count = 0
        for i in range(1, n + 1):
            if i not in used and abs(i - idx) != k:
                count = (count + count_permutations(idx + 1, used | {i})) % MOD

        dp[state] = count
        return count

    print(count_permutations(1, set()))

