#include <stdio.h>
#include <stdlib.h>

#define MAXN 200005

int n, x, y;
int red[MAXN][2];
int blue[MAXN][2];
int red_adj[MAXN][MAXN];
int blue_adj[MAXN][MAXN];
int dist[MAXN][2];
int q[MAXN];
int head, tail;

void bfs(int start, int adj[][MAXN], int d[]) {
    head = tail = 0;
    for (int i = 1; i <= n; i++) {
        d[i] = -1;
    }
    d[start] = 0;
    q[tail++] = start;
    while (head < tail) {
        int u = q[head++];
        for (int v = 1; v <= n; v++) {
            if (adj[u][v] && d[v] == -1) {
                d[v] = d[u] + 1;
                q[tail++] = v;
            }
        }
    }
}

int main() {
    scanf("%d %d %d", &n, &x, &y);
    for (int i = 0; i < n - 1; i++) {
        scanf("%d %d", &red[i][0], &red[i][1]);
        red_adj[red[i][0]][red[i][1]] = red_adj[red[i][1]][red[i][0]] = 1;
    }
    for (int i = 0; i < n - 1; i++) {
        scanf("%d %d", &blue[i][0], &blue[i][1]);
        blue_adj[blue[i][0]][blue[i][1]] = blue_adj[blue[i][1]][blue[i][0]] = 1;
    }
    bfs(x, red_adj, dist[0]);
    bfs(y, blue_adj, dist[1]);
    int ans = -1;
    for (int i = 1; i <= n; i++) {
        if (dist[0][i] != -1 && dist[1][i] != -1) {
            if (ans == -1 || (dist[0][i] + dist[1][i]) % 2 == 0 && dist[0][i] + dist[1][i] < ans) {
                ans = dist[0][i] + dist[1][i];
            }
        }
    }
    if (ans == -1) {
        printf("-1\n");
    } else {
        printf("%d\n", ans + 1);
    }
    return 0;
}
