#include <iostream>
#include <vector>
#include <queue>

using namespace std;

int main() {
    int n, x, y;
    cin >> n >> x >> y;

    vector<tuple<int, int>> red_edges(n - 1);
    for (int i = 0; i < n - 1; ++i) {
        cin >> get<0>(red_edges[i]) >> get<1>(red_edges[i]);
        get<0>(red_edges[i])--; 
        get<1>(red_edges[i])--; 
    }

    vector<tuple<int, int>> blue_edges(n - 1);
    for (int i = 0; i < n - 1; ++i) {
        cin >> get<0>(blue_edges[i]) >> get<1>(blue_edges[i]);
        get<0>(blue_edges[i])--; 
        get<1>(blue_edges[i])--; 
    }

    x--; 
    y--; 

    queue<pair<pair<int, int>, int>> q;
    q.push({{x, y}, 0});

    vector<vector<vector<bool>>> visited(n, vector<vector<bool>>(n, vector<bool>(2, false)));
    visited[x][y][0] = true;

    while (!q.empty()) {
        int cur_x = q.front().first.first;
        int cur_y = q.front().first.second;
        int turn = q.front().second;
        q.pop();

        if (cur_x == cur_y) {
            cout << turn + 1 << endl;
            return 0;
        }

        int next_turn = turn + 1;
        bool sigma_turn = (next_turn % 2 != 0);

        if (sigma_turn) {
            for (const auto& edge : red_edges) {
                int next_x = -1;
                if (get<0>(edge) == cur_x) next_x = get<1>(edge);
                if (get<1>(edge) == cur_x) next_x = get<0>(edge);
                if (next_x != -1 && !visited[next_x][cur_y][next_turn % 2]) {
                    visited[next_x][cur_y][next_turn % 2] = true;
                    q.push({{next_x, cur_y}, next_turn});
                }
            }
             q.push({{cur_x, cur_y}, next_turn});
        } else {
            for (const auto& edge : blue_edges) {
                int next_y = -1;
                if (get<0>(edge) == cur_y) next_y = get<1>(edge);
                if (get<1>(edge) == cur_y) next_y = get<0>(edge);
                if (next_y != -1 && !visited[cur_x][next_y][next_turn % 2]) {
                    visited[cur_x][next_y][next_turn % 2] = true;
                    q.push({{cur_x, next_y}, next_turn});
                }
            }
            q.push({{cur_x, cur_y}, next_turn});
        }
    }

    cout << -1 << endl;
    return 0;
}
