import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int x = sc.nextInt();
        int y = sc.nextInt();
        List<List<Integer>> red = new ArrayList<>();
        List<List<Integer>> blue = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            red.add(new ArrayList<>());
            blue.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int a = sc.nextInt() - 1;
            int b = sc.nextInt() - 1;
            red.get(a).add(b);
            red.get(b).add(a);
        }
        for (int i = 0; i < n - 1; i++) {
            int c = sc.nextInt() - 1;
            int d = sc.nextInt() - 1;
            blue.get(c).add(d);
            blue.get(d).add(c);
        }
        Queue<Pair> q = new LinkedList<>();
        q.add(new Pair(x - 1, y - 1, 0));
        Set<Pair> visited = new HashSet<>();
        visited.add(new Pair(x - 1, y - 1));
        int ans = -1;
        while (!q.isEmpty()) {
            Pair p = q.poll();
            if (p.x == p.y) {
                ans = p.t;
                break;
            }
            if (p.t % 2 == 0) {
                for (int next : blue.get(p.y)) {
                    Pair nextP = new Pair(p.x, next, p.t + 1);
                    if (!visited.contains(nextP)) {
                        visited.add(nextP);
                        q.add(nextP);
                    }
                }
            } else {
                for (int next : red.get(p.x)) {
                    Pair nextP = new Pair(next, p.y, p.t + 1);
                    if (!visited.contains(nextP)) {
                        visited.add(nextP);
                        q.add(nextP);
                    }
                }
            }
        }
        System.out.println(ans);
    }

    static class Pair {
        int x, y, t;

        Pair(int x, int y, int t) {
            this.x = x;
            this.y = y;
            this.t = t;
        }

        @Override
        public boolean equals(Object obj) {
            Pair p = (Pair) obj;
            return x == p.x && y == p.y;
        }

        @Override
        public int hashCode() {
            return Objects.hash(x, y);
        }
    }
}
