#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 998244353

int main() {
    long long N, M;
    scanf("%lld %lld", &N, &M);
    char A[N], B[N], C[M], D[M];
    scanf("%s %s %s %s", A, B, C, D);
    long long ans = 1;
    for (long long i = 0; i < N; i++) {
        if (A[i] == '1' || B[i] == '1') ans = (ans * 2) % MOD;
    }
    for (long long i = 0; i < M; i++) {
        if (C[i] == '1' || D[i] == '1') ans = (ans * 2) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}

