#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<tuple<int, int>> edges(n - 1);
  for (int i = 0; i < n - 1; ++i) {
    cin >> get<0>(edges[i]) >> get<1>(edges[i]);
    get<0>(edges[i])--;
    get<1>(edges[i])--;
  }

  for (int k = 1; k <= n; ++k) {
    long long total_f = 0;
    long long count = 0;

    for (int i = 0; i < (1 << n); ++i) {
      if (__builtin_popcount(i) == k) {
        vector<int> subset;
        for (int j = 0; j < n; ++j) {
          if ((i >> j) & 1) {
            subset.push_back(j);
          }
        }

        int min_subtree_size = -1;
        for (int j = 0; j < (1 << n); ++j) {
          vector<int> subtree_nodes;
          vector<bool> visited(n, false);
          vector<int> q;

          int start_node = -1;
          for (int node_index : subset) {
              if((j >> node_index) &1){
                  start_node = node_index;
                  break;
              }
          }
          if(start_node == -1) continue;

          q.push_back(start_node);
          visited[start_node] = true;
          int head = 0;
          while (head < q.size()) {
            int u = q[head++];
            subtree_nodes.push_back(u);
            for (const auto& edge : edges) {
              int v = -1;
              if (get<0>(edge) == u && !visited[get<1>(edge)]) {
                v = get<1>(edge);
              } else if (get<1>(edge) == u && !visited[get<0>(edge)]) {
                v = get<0>(edge);
              }
              if (v != -1 && ((j >> v) & 1)) {
                q.push_back(v);
                visited[v] = true;
              }
            }
          }

          bool contains_all = true;
          for (int node : subset) {
            bool found = false;
            for (int subtree_node : subtree_nodes) {
              if (node == subtree_node) {
                found = true;
                break;
              }
            }
            if (!found) {
              contains_all = false;
              break;
            }
          }

          if (contains_all) {
            if (min_subtree_size == -1 || (int)subtree_nodes.size() < min_subtree_size) {
              min_subtree_size = subtree_nodes.size();
            }
          }
        }
        total_f = (total_f + min_subtree_size) % 924844033;
        count++;
      }
    }
    cout << total_f << endl;
  }
  return 0;
}
