#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

int main() {
  string s;
  long long k;
  cin >> s >> k;

  for (long long i = 0; i < k; ++i) {
    int index = 0;
    for (int j = 0; j < s.length(); ++j) {
      if (s[j] < 'z') {
        index = j;
        break;
      }
    }
    s[index]++;
    if (s[index] == 'a' + 26) s[index] = 'a';
  }
  cout << s << endl;
  return 0;
}
