s, k = input(), int(input())
s = list(s)
for i in range(len(s)):
    diff = ord('z') - ord(s[i])
    if k <= diff:
        s[i] = chr(ord(s[i]) + k)
        k = 0
        break
    else:
        k -= diff + 1
        s[i] = 'a'
if k > 0:
    for i in range(len(s) - 1, -1, -1):
        if k == 0:
            break
        diff = ord('z') - ord(s[i])
        if k <= diff:
            s[i] = chr(ord(s[i]) + k)
            k = 0
            break
        else:
            k -= diff + 1
            s[i] = 'a'

print("".join(s))

