#include <stdio.h>

int main() {
    long long R, C;
    scanf("%lld %lld", &R, &C);
    long long N;
    scanf("%lld", &N);
    long long r[N], c[N], a[N];
    for (long long i = 0; i < N; i++) {
        scanf("%lld %lld %lld", &r[i], &c[i], &a[i]);
        r[i]--; 
        c[i]--; 
    }
    long long grid[R][C];
    for (long long i = 0; i < R; i++) {
        for (long long j = 0; j < C; j++) {
            grid[i][j] = -1;
        }
    }
    for (long long i = 0; i < N; i++) {
        grid[r[i]][c[i]] = a[i];
    }
    for (long long i = 0; i < R -1; i++) {
        for (long long j = 0; j < C - 1; j++) {
            long long count = 0;
            long long sum1 = 0, sum2 = 0;
            if (grid[i][j] != -1) {
                sum1 += grid[i][j];
                count++;
            }
            if (grid[i][j+1] != -1) {
                sum2 += grid[i][j+1];
                count++;
            }
            if (grid[i+1][j] != -1) {
                sum1 += grid[i+1][j];
                count++;
            }
            if (grid[i+1][j+1] != -1) {
                sum2 += grid[i+1][j+1];
                count++;
            }
            if (count > 2) {
                if (sum1 != sum2) {
                    printf("No\n");
                    return 0;
                }
            }

        }
    }
    printf("Yes\n");
    return 0;
}
