#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long R, C;
  cin >> R >> C;

  long long N;
  cin >> N;

  vector<tuple<long long, long long, long long>> cells(N);
  for (int i = 0; i < N; ++i) {
    cin >> get<0>(cells[i]) >> get<1>(cells[i]) >> get<2>(cells[i]);
  }

  vector<vector<long long>> grid(R, vector<long long>(C, -1));
  for (int i = 0; i < N; ++i) {
    grid[get<0>(cells[i]) - 1][get<1>(cells[i]) - 1] = get<2>(cells[i]);
  }

  bool possible = true;
  for (long long i = 0; i < R - 1; ++i) {
    for (long long j = 0; j < C - 1; ++j) {
      long long a = grid[i][j];
      long long b = grid[i][j + 1];
      long long c = grid[i + 1][j];
      long long d = grid[i + 1][j + 1];

      if (a != -1 && b != -1 && c != -1 && d != -1) {
        if (a + d != b + c) {
          possible = false;
          break;
        }
      } else if (a != -1 && b != -1 && c != -1 && d == -1) {
          grid[i+1][j+1] = b + c -a;
          if(grid[i+1][j+1] < 0) possible = false;
      } else if (a != -1 && b != -1 && c == -1 && d != -1) {
          grid[i+1][j] = a + d - b;
          if(grid[i+1][j] < 0) possible = false;
      } else if (a != -1 && b == -1 && c != -1 && d != -1) {
          grid[i][j+1] = a + d - c;
          if(grid[i][j+1] < 0) possible = false;
      } else if (a == -1 && b != -1 && c != -1 && d != -1) {
          grid[i][j] = b + c - d;
          if(grid[i][j] < 0) possible = false;
      }
    }
    if (!possible) break;
  }

  if (possible) {
    cout << "Yes" << endl;
  } else {
    cout << "No" << endl;
  }

  return 0;
}
