import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long R = sc.nextLong();
        long C = sc.nextLong();
        long N = sc.nextLong();
        long[][] grid = new long[(int) R][(int) C];
        Map<Long, Map<Long, Long>> map = new HashMap<>();
        for (int i = 0; i < N; i++) {
            long r = sc.nextLong();
            long c = sc.nextLong();
            long a = sc.nextLong();
            grid[(int) (r - 1)][(int) (c - 1)] = a;
            map.computeIfAbsent(r, k -> new HashMap<>()).put(c, a);
        }
        for (int i = 0; i < R - 1; i++) {
            for (int j = 0; j < C - 1; j++) {
                long a = grid[i][j];
                long b = grid[i][j + 1];
                long c = grid[i + 1][j];
                long d = grid[i + 1][j + 1];
                if (a == 0 && b == 0 && c == 0 && d == 0) continue;
                if (a == 0) {
                    grid[i][j] = b + c - d;
                    if (grid[i][j] < 0) {
                        System.out.println("No");
                        return;
                    }
                } else if (b == 0) {
                    grid[i][j + 1] = a + d - c;
                    if (grid[i][j + 1] < 0) {
                        System.out.println("No");
                        return;
                    }
                } else if (c == 0) {
                    grid[i + 1][j] = a + d - b;
                    if (grid[i + 1][j] < 0) {
                        System.out.println("No");
                        return;
                    }
                } else if (d == 0) {
                    grid[i + 1][j + 1] = a + b - c;
                    if (grid[i + 1][j + 1] < 0) {
                        System.out.println("No");
                        return;
                    }
                } else if (a + d != b + c) {
                    System.out.println("No");
                    return;
                }
            }
        }
        System.out.println("Yes");
    }
}
