#include <stdio.h>

int main() {
    int N, M, Q;
    scanf("%d %d", &N, &M);
    scanf("%d", &Q);

    int counts[M + 1];
    for (int i = 1; i <= M; ++i) {
        counts[i] = 0;
    }

    for (int i = 0; i < Q; ++i) {
        int a;
        scanf("%d", &a);
        counts[a]++;
    }

    for (int i = 1; i <= M; ++i) {
        if (counts[i] < N) {
            printf("No\n");
            return 0;
        }
    }

    printf("Yes\n");
    return 0;
}
