#include <iostream>
#include <vector>
#include <map>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    int q;
    cin >> q;

    map<int, int> counts;
    for (int i = 0; i < q; ++i) {
        int a;
        cin >> a;
        counts[a]++;
    }

    for (auto const& [key, val] : counts) {
        if (val < (q / n)) {
            cout << "No" << endl;
            return 0;
        }
    }

    cout << "Yes" << endl;
    return 0;
}
