#include <stdio.h>
#include <string.h>

int main() {
    char sa[101], sb[101], sc[101];
    scanf("%s %s %s", sa, sb, sc);
    int pa = 0, pb = 0, pc = 0;
    char turn = 'a';
    while (1) {
        if (turn == 'a') {
            if (pa == strlen(sa)) {
                printf("A\n");
                break;
            }
            turn = sa[pa++];
        } else if (turn == 'b') {
            if (pb == strlen(sb)) {
                printf("B\n");
                break;
            }
            turn = sb[pb++];
        } else {
            if (pc == strlen(sc)) {
                printf("C\n");
                break;
            }
            turn = sc[pc++];
        }
    }
    return 0;
}
