#include <iostream>
#include <string>

using namespace std;

int main() {
    string sa, sb, sc;
    cin >> sa >> sb >> sc;

    string decks[] = {sa, sb, sc};
    int turn = 0;

    while (true) {
        if (decks[turn].empty()) {
            cout << (char)('A' + turn) << endl;
            break;
        }

        char card = decks[turn][0];
        decks[turn] = decks[turn].substr(1);

        if (card == 'a') turn = 0;
        else if (card == 'b') turn = 1;
        else turn = 2;
    }

    return 0;
}
