import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String sa = scanner.next();
        String sb = scanner.next();
        String sc = scanner.next();
        scanner.close();

        int pa = 0;
        int pb = 0;
        int pc = 0;
        char turn = 'a';

        while (true) {
            if (turn == 'a') {
                if (pa == sa.length()) {
                    System.out.println("A");
                    break;
                }
                char card = sa.charAt(pa++);
                if (card == 'a') turn = 'a';
                else if (card == 'b') turn = 'b';
                else turn = 'c';
            } else if (turn == 'b') {
                if (pb == sb.length()) {
                    System.out.println("B");
                    break;
                }
                char card = sb.charAt(pb++);
                if (card == 'a') turn = 'a';
                else if (card == 'b') turn = 'b';
                else turn = 'c';
            } else {
                if (pc == sc.length()) {
                    System.out.println("C");
                    break;
                }
                char card = sc.charAt(pc++);
                if (card == 'a') turn = 'a';
                else if (card == 'b') turn = 'b';
                else turn = 'c';
            }
        }
    }
}
