#include <stdio.h>
#include <string.h>
#include <stdlib.h>

long long calculate(char *s) {
    long long res = 0;
    long long num = 0;
    char op = '+';
    for (int i = 0; i < strlen(s); i++) {
        if (s[i] == '+') {
            if (op == '+') res += num;
            else res -= num;
            num = 0;
            op = '+';
        } else if (s[i] == '-') {
            if (op == '+') res += num;
            else res -= num;
            num = 0;
            op = '-';
        } else {
            num = num * 10 + (s[i] - '0');
        }
    }
    if (op == '+') res += num;
    else res -= num;
    return res;
}

int main() {
    char s[15];
    scanf("%s", s);
    long long sum = 0;
    int n = strlen(s);
    for (int i = 0; i < (1 << (n - 1)); i++) {
        char temp[15] = "";
        int k = 0;
        for (int j = 0; j < n; j++) {
            temp[k++] = s[j];
            if (j < n - 1 && (i >> j) & 1) {
                temp[k++] = '+';
            }
        }
        temp[k] = '\0';
        sum += calculate(temp);

    }
    printf("%lld\n", sum);
    return 0;
}
