int main() {
  string s;
  cin >> s;

  long long total_sum = 0;
  vector<string> formulas;
  formulas.push_back(s);

  for (int i = 0; i < (1 << (s.length() - 1)); ++i) {
    string current_formula = "";
    current_formula += s[0];
    bool prev_plus = false;
    for (int j = 0; j < s.length() - 1; ++j) {
      if ((i >> j) & 1) {
        if (!prev_plus) {
          current_formula += "+";
          prev_plus = true;
        }
      } else {
        prev_plus = false;
      }
      current_formula += s[j + 1];
    }
    bool valid = true;
    for (size_t k = 0; k < current_formula.length() - 1; ++k) {
      if (current_formula[k] == '+' && current_formula[k+1] == '+') {
        valid = false;
        break;
      }
    }
    if (valid) {
      long long num = 0;
      long long cur = 0;
      for (char c : current_formula) {
        if (isdigit(c)) {
          cur = cur * 10 + (c - '0');
        } else {
          num += cur;
          cur = 0;
        }
      }
      num += cur;
      total_sum += num;
    }
  }
  cout << total_sum << endl;
  return 0;
}
