import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        long sum = 0;
        int n = s.length();
        for (int i = 0; i < (1 << (n - 1)); i++) {
            String formula = "";
            long currentNum = 0;
            boolean plus = false;
            for (int j = 0; j < n; j++) {
                currentNum = currentNum * 10 + (s.charAt(j) - '0');
                if (j == n - 1) {
                    formula += currentNum;
                } else if ((i >> j) % 2 == 1) {
                    formula += currentNum + "+";
                    currentNum = 0;
                }
            }
            sum += calculate(formula);

        }
        System.out.println(sum);
    }
    public static long calculate(String formula) {
        String[] nums = formula.split("\\+");
        long sum = 0;
        for (String num : nums) {
            sum += Long.parseLong(num);
        }
        return sum;
    }
}
