#include <stdio.h>

int main() {
    long long H, W, N;
    scanf("%lld %lld %lld", &H, &W, &N);
    long long a[N], b[N];
    for (long long i = 0; i < N; i++) {
        scanf("%lld %lld", &a[i], &b[i]);
    }
    long long count[10] = {0};
    for (long long i = 1; i <= H - 2; i++) {
        for (long long j = 1; j <= W - 2; j++) {
            long long black_cells = 0;
            for (long long x = i; x <= i + 2; x++) {
                for (long long y = j; y <= j + 2; y++) {
                    for (long long k = 0; k < N; k++) {
                        if (a[k] == x && b[k] == y) {
                            black_cells++;
                            break;
                        }
                    }
                }
            }
            count[black_cells]++;
        }
    }
    for (long long i = 0; i < 10; i++) {
        printf("%lld\n", count[i]);
    }
    return 0;
}

