#include <iostream>
#include <vector>

using namespace std;

int main() {
    long long h, w, n;
    cin >> h >> w >> n;

    vector<pair<long long, long long>> painted(n);
    for (int i = 0; i < n; ++i) {
        cin >> painted[i].first >> painted[i].second;
    }

    vector<long long> counts(10, 0);
    for (long long i = 1; i <= h - 2; ++i) {
        for (long long j = 1; j <= w - 2; ++j) {
            int black_count = 0;
            for (long long row = i; row <= i + 2; ++row) {
                for (long long col = j; col <= j + 2; ++col) {
                    for (int k = 0; k < n; ++k) {
                        if (painted[k].first == row && painted[k].second == col) {
                            black_count++;
                            break;
                        }
                    }
                }
            }
            counts[black_count]++;
        }
    }

    for (int i = 0; i < 10; ++i) {
        cout << counts[i] << endl;
    }

    return 0;
}
