#include <stdio.h>
#include <stdlib.h>
#include <string.h>

long long calculate(char *s) {
    long long res = 0;
    long long num = 0;
    int i = 0;
    while (s[i] != '\0') {
        if (s[i] == '+') {
            res += num;
            num = 0;
        } else {
            num = num * 10 + (s[i] - '0');
        }
        i++;
    }
    res += num;
    return res;
}

int main() {
    char s[15];
    scanf("%s", s);
    int n = strlen(s);
    long long sum = 0;
    for (int i = 0; i < (1 << (n - 1)); i++) {
        char temp[15] = "";
        int k = 0;
        for (int j = 0; j < n; j++) {
            temp[k++] = s[j];
            if (j < n - 1 && (i >> j) & 1) {
                temp[k++] = '+';
            }
        }
        temp[k] = '\0';
        sum += calculate(temp);
    }
    printf("%lld\n", sum);
    return 0;
}
