#include <iostream>
#include <string>
#include <vector>

using namespace std;

long long eval(string s) {
  long long res = 0;
  long long cur = 0;
  for (char c : s) {
    if (isdigit(c)) {
      cur = cur * 10 + (c - '0');
    } else {
      res += cur;
      cur = 0;
    }
  }
  res += cur;
  return res;
}

int main() {
  string s;
  cin >> s;

  long long total_sum = 0;
  long long n = s.length();
  for (long long i = 0; i < (1LL << (n - 1)); ++i) {
    string cur = "";
    string formula = "";
    for (long long j = 0; j < n; ++j) {
      cur += s[j];
      if (j < n - 1 && (i & (1LL << j))) {
        formula += cur;
        formula += "+";
        cur = "";
      }
    }
    formula += cur;
    total_sum += eval(formula);
  }
  cout << total_sum << endl;
  return 0;
}
