import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        long ans = 0;
        for (int i = 0; i < (1 << (s.length() - 1)); i++) {
            String cur = "";
            String num = "";
            for (int j = 0; j < s.length(); j++) {
                num += s.charAt(j);
                if (j == s.length() - 1 || (i >> j) % 2 == 1) {
                    cur += num;
                    if (j != s.length() - 1) cur += "+";
                    num = "";
                }
            }
            String[] nums = cur.split("\\+");
            long sum = 0;
            for (String num_str : nums) {
                sum += Long.parseLong(num_str);
            }
            ans += sum;
        }
        System.out.println(ans);
    }
}
