#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long h, w, n;
  cin >> h >> w >> n;

  vector<pair<long long, long long>> painted(n);
  for (int i = 0; i < n; ++i) {
    cin >> painted[i].first >> painted[i].second;
  }

  vector<long long> counts(10, 0);
  for (long long i = 1; i <= h - 2; ++i) {
    for (long long j = 1; j <= w - 2; ++j) {
      int black_cells = 0;
      for (long long x = i; x <= i + 2; ++x) {
        for (long long y = j; y <= j + 2; ++y) {
          bool is_painted = false;
          for (int k = 0; k < n; ++k) {
            if (painted[k].first == x && painted[k].second == y) {
              is_painted = true;
              break;
            }
          }
          if (is_painted) {
            black_cells++;
          }
        }
      }
      counts[black_cells]++;
    }
  }

  for (int i = 0; i < 10; ++i) {
    cout << counts[i] << endl;
  }

  return 0;
}
