#include <stdlib.h>
#include <limits.h>

#define MAXN 100005
#define MAXM 200005

typedef struct {
    int to;
    int company;
} Edge;

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    Edge edges[MAXM];
    for (int i = 0; i < m; i++) {
        scanf("%d %d %d", &edges[i].to, &edges[i].to, &edges[i].company);
        edges[i].to--; 
    }

    int dist[MAXN][1000001];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= 1000000; j++) {
            dist[i][j] = INT_MAX;
        }
    }
    dist[0][0] = 0;

    for (int i = 0; i < n -1; i++) {
        for (int j = 0; j < m; j++) {
            int u = edges[j].to;
            int v = edges[j].to;
            int company = edges[j].company;
            for (int k = 0; k <= 1000000; k++) {
                if (dist[u][k] != INT_MAX) {
                    if (dist[v][k] > dist[u][k]) {
                        dist[v][k] = dist[u][k];
                    }

                }
            }
        }
    }


    int min_fare = INT_MAX;
    for (int i = 0; i <= 1000000; i++) {
        if (dist[n - 1][i] != INT_MAX) {
            min_fare = dist[n-1][i];
            break;
        }
    }

    if (min_fare == INT_MAX) {
        printf("-1\n");
    } else {
        printf("%d\n", min_fare);
    }

    return 0;
}
