import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<int[]>[] graph = new List[n + 1];
        for (int i = 1; i <= n; i++) {
            graph[i] = new ArrayList<>();
        }
        for (int i = 0; i < m; i++) {
            int p = sc.nextInt();
            int q = sc.nextInt();
            int c = sc.nextInt();
            graph[p].add(new int[]{q, c});
            graph[q].add(new int[]{p, c});
        }
        Queue<int[]> queue = new LinkedList<>();
        queue.offer(new int[]{1, 0, -1});
        int[] dist = new int[n + 1];
        Arrays.fill(dist, Integer.MAX_VALUE);
        dist[1] = 0;
        while (!queue.isEmpty()) {
            int[] curr = queue.poll();
            int u = curr[0];
            int cost = curr[1];
            int prevCompany = curr[2];
            for (int[] edge : graph[u]) {
                int v = edge[0];
                int company = edge[1];
                int newCost = cost + (prevCompany == -1 || prevCompany != company ? 1 : 0);
                if (newCost < dist[v]) {
                    dist[v] = newCost;
                    queue.offer(new int[]{v, newCost, company});
                }
            }
        }
        if (dist[n] == Integer.MAX_VALUE) {
            System.out.println(-1);
        } else {
            System.out.println(dist[n] + 1);
        }
    }
}
